				GetCookie : function (name) {
					try {
						var i, arr, c, x, y;
						arr = document.cookie.split("; ");
						for (i = 0; i < arr.length; i++) {
							c = arr[i];
							x = c.substr(0, c.indexOf("="));
							x = x.replace(/^\s+|\s+$/g, "");
							if (x == name) {
								y = c.substr(c.indexOf("=") + 1);
								y = unescape(y);
								return y;
							}
						}
					} catch (err) {
						iLog("GetCookie", err, Log.Type.Error, true);
					}
					return "";
				},
